import React, { Component } from 'react';

class Article extends Component {
    constructor() {
        super();
        this.state = {
            data: null,
            loaded: false
        }
    }

    componentDidMount () {
        this.setState({
            data: this.props.data,
            loaded: true
        });
    }

    render() {
        return (
            <div>
                { this.state.loaded ? (
                    <div>{this.state.data.code}</div>
                ):(
                    <div>Loading</div>
                )}
            </div>
        );
    }
}

export default Article
