import React, { Component } from 'react';
import "../../styles/pages/article.scss";

class Article extends Component {
    constructor() {
        super();
        this.state = {
            data: null,
            loaded: false
        }
    }

    componentDidMount() {
        this.setState({
            data: this.props.data,
            loaded: true
        });
    }

    render() {
        return (
            <div>
                {this.state.loaded ? (
                    <img src={this.state.data.article_image}>
                        teste
                    </img>
                ) : (
                        <div>Loading</div>
                    )
                }
            </div>
        );
    }
}

export default Article
