import React, { Component } from 'react';
import "../../styles/pages/article.scss";

class Article extends Component {
    constructor() {
        super();
        this.state = {
            data: null,
            loaded: false
        }
    }

    componentDidMount() {
        this.setState({
            data: this.props.data,
            loaded: true
        });
    }

    Join (data) {
        return data.join(", ");
    }

    render() {
        return (
            <div className="article">
                {this.state.loaded ? (
                    <div className="article__content">
                        <div className="article__content__branding">
                            <div className="article__content__branding__content">
                                <img className="article__content__branding__content--image" src={this.state.data.site_favicon} />
                                <span className="article__content__branding__content--name">{this.state.data.site_name}</span>
                            </div>
                        </div>
                        <div className="article__content__heading">
                            <div className="article__content__heading__title">
                                <h1 className="article__content__heading__title--text">{
                                    this.state.data.article_title
                                }</h1>
                            </div>
                            <img className="article__content__heading--image" src={this.state.data.article_image} />
                            <div className="article__content__heading__keywords">

                                <span className="article__content__heading__keywords--text">
                                    {
                                        this.Join(this.state.data.keywords)
                                    }
                                </span>
                            </div>

                            <div>

                            </div>
                        </div>
                    </div>
                ) : (
                        <div>Loading</div>
                    )}
            </div>
        );
    }
}

export default Article
