import React, { Component } from 'react';
import '../../styles/pages/homepage.scss';

import LoadingArticle from "../abstracts/loading";
import ErrorPopup from "../abstracts/error";

class Homepage extends Component {
  constructor() {
    super();
    this.state = {
      url: null,
      loading: false,
      error: false
    };
  }



  ChangedUrl (e) {
    this.setState({
      url: e.target.value
    });
  }

  ClearURLField () {
    document.getElementById("url_input").value = "";
    this.setState({
      url: null
    });
  }

  Simplify () {
    this.setState({
      loading: true
    })


    var payload = new Headers();
    // payload.append("article-url","https://oglobo.globo.com/brasil/apos-ir-protesto-com-criticas-ao-stf-bolsonaro-diz-que-estara-onde-povo-estiver-1-24455623")

    fetch(`http://127.0.0.1:5000/parser/json`, {
      method: "GET",
      headers: payload,
      mode: "cors",
      cache:"default"
    })
    .then(res => res.json())
    .then(json => this.ProcessData(json));
  }

  ProcessData (json) {
    setTimeout(() => {
      this.setState({loading: false});
    }, 200);

    if (json.error) {
      this.setState({
        error: true,
      })
    } else {
      this.setState({
        error: false,
      })
    }
  }

  render() {
    return (
      <div className="homepage">
        { this.state.loading ? (
          <LoadingArticle />
        ) : (
        <div className="homepage__container">
          <div className="homepage__container__logo">
            <h1 className="homepage__container__logo__texts">
              <span className="homepage__container__logo__texts--small">Simplify</span>
              <span className="homepage__container__logo__texts--bigger">Journal</span>
            </h1>
          </div>
          <div className="homepage__container__actions">
            <div className="homepage__container__actions__input">
              <input onChange={e => this.ChangedUrl(e)} id="url_input" className="homepage__container__actions__input--input" placeholder="Enter article to simplify" />
              <button onClick={e => this.ClearURLField()} className={`homepage__container__actions__input--clear ${this.state.url != null && 'active'}`}>X</button>
            </div>
            <button onClick={() => this.Simplify()} className={`homepage__container__actions--button ${this.state.url && 'active'}`}>SIMPLIFY</button>
            <a className="homepage__container__actions--example" 
            href="/https://oglobo.globo.com/brasil/apos-ir-protesto-com-criticas-ao-stf-bolsonaro-diz-que-estara-onde-povo-estiver-1-24455623"
            >See an example</a>
          </div>
        </div>
        )}

        <ErrorPopup show={this.state.error} />
      </div>
    );
  }
}

export default Homepage;
